import React, { useRef, useState } from 'react';
import { checkValidData } from '../utils/validate';
import Header from './Header';

const Login = () => {

    const [isSignInForm, setIsSignInForm] = useState(true);
    const [errorMessage, setErrorMessage] = useState(null);

    const name = useRef(null);
    const phone = useRef(null);
    const email = useRef(null);
    const password = useRef(null);

    const handleButtonClick = () => {
        const emailValue = email.current.value;
        const passwordValue = password.current.value;

        console.log('Form Data:', {
            email: emailValue,
            password: passwordValue,
            name: name.current?.value,
            phone: phone.current?.value,
        });

        const message = checkValidData(emailValue, passwordValue);
        setErrorMessage(message);

        if (!message) {
            if (isSignInForm) {
                console.log('Sign In Data:', { email: emailValue, password: passwordValue });
            } else {
                console.log('Sign Up Data:', {
                    name: name.current.value,
                    phone: phone.current.value,
                    email: emailValue,
                    password: passwordValue,
                });
            }
        }
    }

    const toggleSignInForm = () => {
        setIsSignInForm(!isSignInForm);
    };

    return (
        <div className='relative w-screen h-screen'>
            <Header />
            <div className='absolute inset-0'>
                <img className='w-full h-full object-cover' src='./images/Background.png' alt='Background anime wallpaper' />
            </div>

            <div className='absolute inset-0 flex items-center justify-center p-12 bg-black bg-opacity-50 text-white'>
                {/* Flip Card */}
                <div className={`flip-card ${isSignInForm ? '' : 'flipped'}`} style={{ width: '400px', height: '500px' }}>
                    <div className="flip-card-inner w-full h-full">
                        
                        {/* Sign In Form */}
                        <form onSubmit={(e) => e.preventDefault()} className='flip-card-front w-full h-full p-10 rounded-lg shadow-lg bg-gradient-to-b from-green-400 via-blue-500 to-blue-800'>
                            <h1 className='font-bold text-3xl mb-4'>Sign In</h1>
                            <input
                                ref={email}
                                type='text'
                                placeholder='Email Address'
                                className='block w-full mb-6 p-2 border border-gray-800 bg-gray-600 outline-none rounded-md'
                            />
                            <input
                                ref={password}
                                type='password'
                                placeholder='Password'
                                className='block w-full mb-6 p-2 border border-gray-800 bg-gray-600 outline-none rounded-md'
                            />
                            <p className='text-red-500 text-base'>{errorMessage}</p>
                            <button type='button' className='w-full mt-12 py-2 bg-yellow-500 text-black font-bold rounded hover:bg-yellow-600' onClick={handleButtonClick}>
                                Sign In
                            </button>
                            <p className='text-center text-white mt-6 cursor-pointer'>
                                New to Animeflix? <span className='text-green-300 hover:text-green-500' onClick={toggleSignInForm}>SIGN UP.</span>
                            </p>
                            <p className="text-center mt-4 text-base text-gray-200 font-light leading-relaxed">
                                ðŸš€ Ready for a new adventure? ðŸŒŸ Log in now and explore AnimeFlix â€“ your gateway to epic battles, heartwarming stories, and more! ðŸŽ¥âœ¨
                            </p>
                        </form>

                        {/* Sign Up Form */}
                        <form onSubmit={(e) => e.preventDefault()} className='flip-card-back w-full h-full p-10 rounded-lg shadow-lg bg-gradient-to-b from-green-400 via-blue-500 to-blue-800'>
                            <h1 className='font-bold text-3xl mb-4'>Sign Up</h1>
                            <input
                                ref={name}
                                type='text'
                                placeholder='Full Name'
                                className='block w-full mb-6 p-2 border border-gray-800 bg-gray-600 outline-none rounded-md'
                            />
                            <input
                                ref={email}
                                type='text'
                                placeholder='Email Address'
                                className='block w-full mb-6 p-2 border border-gray-800 bg-gray-600 outline-none rounded-md'
                            />
                            <input
                                ref={phone}
                                type='text'
                                placeholder='Phone No.'
                                className='block w-full mb-6 p-2 border border-gray-800 bg-gray-600 outline-none rounded-md'
                            />
                            <input
                                ref={password}
                                type='password'
                                placeholder='Password'
                                className='block w-full mb-6 p-2 border border-gray-800 bg-gray-600 outline-none rounded-md'
                            />
                            <p className='text-red-500 text-base'>{errorMessage}</p>
                            <button type='button' className='w-full mt-8 py-2 bg-yellow-500 text-black font-bold rounded hover:bg-yellow-600' onClick={handleButtonClick}>
                                Sign Up
                            </button>
                            <p className='text-center text-white mt-8 cursor-pointer'>
                                Already a member? <span className='text-green-300 hover:text-green-500' onClick={toggleSignInForm}>SIGN IN.</span>
                            </p>
                        </form>
                        
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Login;
