import React from 'react';
import { FaPlay, FaInfoCircle } from 'react-icons/fa';

function VideoTitle() {
    return (
        <div className='absolute inset-0 flex items-center justify-center p-10 z-20'>
            <div className='text-center bg-black bg-opacity-70 p-8 rounded-lg shadow-lg'>
                <h1
                    className='text-4xl font-bold text-white mb-4'
                    style={{
                        textShadow: '2px 2px 4px rgba(0, 0, 0, 0.6)',
                    }}
                >
                    Easy Development
                </h1>
                <p
                    className='text-lg text-white mb-6'
                    style={{
                        textShadow: '1px 1px 2px rgba(0, 0, 0, 0.5)',
                    }}
                >
                    Learn how to develop with ease. Keep learning and exploring to face the world confidently.
                </p>
                <div className='flex justify-center gap-4'>
                    <button
                        className='flex items-center text-base text-white font-medium bg-yellow-500 px-4 py-2 hover:bg-yellow-600 rounded-lg shadow-md'
                        style={{
                            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.2)',
                        }}
                    >
                        Play <FaPlay className='ml-2 text-xl' />
                    </button>
                    <button
                        className='flex items-center text-base text-white font-medium px-4 py-2 rounded-lg bg-gradient-to-t from-blue-800 via-blue-500 to-green-400 shadow-md hover:opacity-80'
                        style={{
                            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.2)',
                        }}
                    >
                        Watch More <FaInfoCircle className='ml-2 text-xl' />
                    </button>
                </div>
            </div>
        </div>
    );
}

export default VideoTitle;
