import React from 'react'
import Header from './header';

import { checkValidData } from '../utils/validate';

function login() {

  const [isSignInForm, setIsSignInForm] = useState(true);
  const [errorMessage, setErrorMessage] = useState(null);

  const email = useRef(null);
  const password = useRef(null);

  const handleButtonClick = () => {
    // let first validate the data
    const message = checkValidData(email.current.value, password.current.value);
    setErrorMessage(message);

    // after validation checking is done then i can proceed with signIn/signUp
    

  }

  const toggleSignInForm = () => {
    setIsSignInForm(!isSignInForm);
  };

  return (
    <div className='relative w-screen h-screen'>
      <Header />
      <div className='absolute inset-0'>
        <img className='brightness-90 w-full h-full object-cover' src='./images/background.png' alt='Background anime wallpaper' />
      </div>

      <form onSubmit={(e) => e.preventDefault()} className='absolute inset-0 flex items-center justify-center p-12 bg-black bg-opacity-50 text-white'>
        <div className='bg-opacity-70 p-10 w-4/12 rounded-lg shadow-lg bg-gradient-to-b from-green-400 via-blue-500 to-blue-800 via-black/70 to-transparent'>
          <h1 className='font-bold text-3xl mb-4'>
            {isSignInForm ? 'Sign In' : 'Sign Up'}
          </h1>
          {
            !isSignInForm && (
              <input
                type='text'
                placeholder='Full Name'
                className='block w-full mb-6 p-2 border border-gray-800 bg-gray-600 rounded'
              />
            )
          }
          <input
            ref={email}
            type='text'
            placeholder='Email Address'
            className='block w-full mb-6 p-2 border border-gray-800 bg-gray-600 rounded'
          />

          {
            !isSignInForm && (
              <input
                type='text'
                placeholder='Phone No.'
                className='block w-full mb-6 p-2 border border-gray-800 bg-gray-600 rounded'
              />
            )
          }

          <input
            ref={password}
            type='password'
            placeholder='Password'
            className='block w-full mb-1 p-2 border border-gray-800 bg-gray-600 rounded'
          />

          <p className='text-red-500 text-base'>{errorMessage}</p>

          <button type='submit' className='w-full mt-8 py-2 bg-yellow-500 text-black font-bold rounded hover:bg-yellow-600' onClick={handleButtonClick}>
            {isSignInForm ? "Sign In" : "Sign Up"}
          </button>

          <p className='text-center text-white mt-8 cursor-pointer'>
            {isSignInForm
              ? <>
                New to Animeflix? <a href='#' className='text-green-300 hover:text-green-500' onClick={toggleSignInForm}>SIGN UP.</a>
              </>
              : <>
                Already a member? <a href='#' className='text-green-300 hover:text-green-500' onClick={toggleSignInForm}>SIGN IN.</a>
              </>
            }
          </p>
        </div>
      </form>

    </div>
  )
}

export default login
